    <script>
        document.addEventListener('DOMContentLoaded', function () {
            setTimeout(function(){
                $('a[data-state=<?= strtolower($estadosBrasileiros[$details->region]) ?? ""; ?>]').click();
            }, 1000);
            //Exibe box de parceiros correspondetes em cada estado quando houver click.
            $('#map .state').click(function () {
                var estado = $(this).data('state');
                if (estado) {
                    $.ajax({
                        type: "GET",
                        url: "/wp-json/wp/v2/return-form-brasil?uf=" + estado,
                        beforeSend: function (bf) {
                            $('.container-itens-brasil').empty();
                        },
                        success: function (rs) {
                            let result = rs ? JSON.parse(rs) : {};
                            let htmlItem = "";
                            for (let i = 0; i < result.length; i++) {
                                htmlItem += '<div class="itens mb-4">';
                                htmlItem += '<strong>' + result[i].post_title + '</strong>';
                                if(typeof result[i] !== 'undefined'){
                                    for (let z = 0; z < result[i].meta.local; z++) {
                                        htmlItem += '<address>';
                                        htmlItem += result[i].meta[`local_${z}_segmento`] || "";
                                        htmlItem += "<br>";
                                        htmlItem += result[i].meta[`local_${z}_endereco`].replace(/(?:\r\n|\r|\n)/g, '<br>');
                                        htmlItem += '</address>';
                                    }
                                }
                                htmlItem += '</div>';
                            }
                            $('.title-uf').text("Estado: "+estado.toUpperCase());
                            if(result.length <= 0) {
                                htmlItem += '<h4 class="text-center my-5">Ainda não estamos presentes neste estado.</h4>';
                            }
                            $('.container-itens-brasil').append(htmlItem);
                        }
                    })
                }

                // Reseta o estado e seu label para a cor padrão
                // $('.state .label_icon_state').css({'fill': '#666'});
                $('.state .shape').css({'fill': 'rgba(67, 41, 124, .1)'});

                // Colore o estado selecionado
                $('#state_' + estado + ' .label_icon_state').css({'fill': '#FFF'});
                $('#state_' + estado + ' .shape').css({'fill': '#ED0973'});

            });
            $('#map .state').click(function (e) {
                e.preventDefault();
            });


            var countryElements = document.getElementById('countries').childNodes;
            var countryCount = countryElements.length;
            for (var i = 0; i < countryCount; i++) {
                countryElements[i].onclick = function() {
                  // alert(this.getAttribute('data-name'))
                  $("#map-inter").html(this.getAttribute('data-name'))
                    if (this.getAttribute('data-name')) {
                        $.ajax({
                            type: "GET",
                            url: "/wp-json/wp/v2/return-form-world?country=" + this.getAttribute('data-name'),
                            beforeSend: function (bf) {
                                $('.container-itens').empty();
                            },
                            success: function (rs) {
                                let result = rs ? JSON.parse(rs) : {};
                                let htmlItem = "";
                                for (let i = 0; i < result.length; i++) {

                                    htmlItem += '<div class="itens mb-4">';
                                    htmlItem += '<strong>' + result[i].post_title + '</strong>';
                                    if(typeof result[i] !== 'undefined'){
                                        for (let z = 0; z < result[i].meta.local; z++) {
                                            htmlItem += '<address>';
                                            htmlItem += result[i].meta[`local_${z}_segmento`] || "";
                                            htmlItem += "<br>";
                                            htmlItem += result[i].meta[`local_${z}_endereco`].replace(/(?:\r\n|\r|\n)/g, '<br>');
                                            htmlItem += '</address>';
                                        }
                                    }
                                    htmlItem += '</div>';
                                }
                                // $('.title-uf').text("Estado: "+estado.toUpperCase());
                                if(result.length <= 0) {
                                    htmlItem += '<h4 class="text-center my-5">Ainda não estamos presentes neste local.</h4>';
                                }
                                $('.container-itens').append(htmlItem);
                            }
                        })
                    }
                }
            }
        })
    </script>
